package com.leekli.demo.utils;


import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.InputStream;
import java.io.OutputStream;

import org.springframework.util.Base64Utils;

public class Base64ImageTest {
	public static void main(String[] args) {
		//将base64编码的图片，保存为图片
		String str = "data:image/jpg;base64,";
		try {
			byte[] img = Base64Utils.decode(str.split(",")[1].getBytes());
			OutputStream out = new FileOutputStream("D:\\aaa.jpg");
			out.write(img);
			out.flush();  
			out.close();
		} catch (Exception e) {
			e.printStackTrace();
		}
		
		
		
		//用base64编码图片，并解析图片
		try {
			InputStream in = new FileInputStream("D:\\a.jpg");
			byte[] b =  new byte[in.available()];
			in.read(b);
			byte[] imgbyte = Base64Utils.encode(b);
			String base64Img = new String(imgbyte);
			System.out.println(base64Img);
			
			byte[] img = Base64Utils.decode(base64Img.getBytes());
			OutputStream out = new FileOutputStream("D:\\aa.jpg");
			out.write(img);
			out.flush();  
			out.close();
		} catch (Exception e1) {
			// TODO Auto-generated catch block
			e1.printStackTrace();
		}
		
		
		
		

		
	
	}
}

